mapper.insert:

INSERT 
INTO	TB_MEMBER (
		USER_NO
		,USER_NAME
		,USER_ID
		,USER_PWD
		,NICKNAME
		,GENDER
		,EMAIL
		,PHONE
		,ROLE
		,CREATE_AT
		,STATUS
		)

VALUES	(
		SEQ_UNO.NEXTVAL 
		,#{userName}
		,#{userId} 
		,#{userPwd} 
		,#{nickName} 
		,#{gender} 
		,#{email} 
		,#{phone} 
		,#{role}
		,DEFAULT
		,DEFAULT
		)


	SELECT SEQ_UNO.CURRVAL as userNo FROM DUAL


INSERT 
INTO	TB_MEMBER (
		USER_NO
		,USER_NAME
		,USER_ID
		,USER_PWD
		,NICKNAME
		,GENDER
		,EMAIL
		,PHONE
		,ROLE
		,FILE_URL
		,CREATE_AT
		,STATUS
		)

VALUES	(
		SEQ_UNO.NEXTVAL
		,#{userName}
		,#{userId} 
		,#{userPwd} 
		,#{nickName} 
		,#{gender} 
		,#{email} 
		,#{phone} 
		,#{role}
		,#{fileUrl}
		,DEFAULT
		,DEFAULT
		)

mapper.update:
UPDATE
		TB_MEMBER
SET
		NICKNAME = #{nickName}
		,PHONE = #{phone}
		,FILE_URL = #{fileUrl}
WHERE
		USER_NO = #{userNo}

UPDATE 
		TB_MEMBER
SET 
		FILE_URL = #{fileUrl}
WHERE 
		USER_NO = #{userNo}
